mapper.select:

select *
from user 
where id = #{id} and pwd = #{pwd}

mapper.update:

update user
set pwd = #{pwd}, name = #{name}, age = #{age} 
where id = #{id}

mapper.delete:

delete 
from user
where id = #{id} and pwd = #{pwd}

mapper.select:

select *
from user
where id = #{id} and name = #{name}


select count(*)
from user
where id = #{id}
